import React from 'react';
import './index.scss';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import { red } from 'color-name';



const toggleBar = function(){

  document.querySelector("ul").style.display==="none"
  ?document.querySelector("ul").style.display="block"
  :document.querySelector("ul").style.display="none";

}

const changeColor = function(e){

}

function Navigation() {
  return (

    <nav>
      <button onClick={toggleBar}>Burger</button>
        <ul className="list">
     <li id="1" onClick={(e) => changeColor(e)}>
       <Link to="/">Home</Link>
     </li>
     <li id="2" onClick={(e) => changeColor(e)}>
       <Link to="/about">About</Link>
     </li>
     <li id="3" onClick={(e) => changeColor(e)}>
       <Link to="/projects">Projects</Link>
     </li>
     <li id="4" onClick={(e) => changeColor(e)}>
       <Link to="/no-match">NoMatch</Link>
     </li>
        </ul>
    </nav>

  );
}

export default Navigation;
