import React from 'react';
import './index.scss';
import {
    BrowserRouter as Router,
    Link
} from "react-router-dom";

const toggleBar = function(){
  document.querySelector("ul").style.display==="none"
  ?document.querySelector("ul").style.display="block"
  :document.querySelector("ul").style.display="none";
}



const changeColor = function(id){
  document.getElementById(id).style.color= "red";
}

function Navigation() {
  return (
    <nav>
      <button onClick={toggleBar}>Burger</button>
      <ul className="list"> 
        <li id="1" onClick={() => changeColor(1)}>
          <Link to="/">Home</Link> 
        </li>
        <li id="2" onClick={() => changeColor(2)}>
          <Link to="/about">About</Link>
        </li>
        <li id="3" onClick={() => changeColor(3)}>
          <Link to="/projects">Projects</Link>
        </li> 
        <li id="4" onClick={() => changeColor(4)}>
          <Link to="/no-match">NoMatch</Link>
        </li>
      </ul>
    </nav>
  );
}

export default Navigation;
